import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import "../Css/utils.css";
import "../Css/VideoPlayer.css";
import Hls from "hls.js";
import BackIcn from "../assets/back.png";

import FullScreenIcn from "../assets/player-icons/fullscreen.png";
import PlayIcn from "../assets/player-icons/fullscreen.png";
import PauseIcn from "../assets/player-icons/fullscreen.png";
import SettingIcn from "../assets/player-icons/fullscreen.png";
import VolumeIcn from "../assets/player-icons/fullscreen.png";


const VideoPlayer = (props) => {
  useEffect(() => {
    const video = document.getElementById('video');
    const hls = new Hls();
    // const videoSrc = `https://hls-test.moneyyapp.in/videos-output/${data.fileEndpoint}/360.m3u8`;             
    const videoSrc = `https://hls-test.moneyyapp.in/videos-output/20230613125013-XII-ITF-L-4-Equation-Series.mp4/360.m3u8`;             
    hls.loadSource(videoSrc);
    hls.attachMedia(video);
    hls.on(Hls.Events.MANIFEST_PARSED, function() { 
      // video.play(); 
    });
  },[])
  return(
    <div>
      {/* BACK BUTTON */}
      <div>
        <img src={BackIcn}/>
      </div>
      <video id="video" controls style={{width:"100%",height:"100%"}}></video>
    </div>
  )
}
const mapStateToProps = (state) => {
  return {
    //Here State.post is
    //Coming From -> "./reducers/index.js"
    //where "item" is defined under combineReducers
    appData: state.appData,
  };
};

export default connect(mapStateToProps, {})(VideoPlayer);
